class ScrollingLayer{constructor(scene,speed,texture,{frame:frame=null,y:y=null,overlap:overlap=1}={}){let t=this;t.scene=scene,t.y=y,t.speed=speed,t.overlap=overlap,t.texture=texture,t.frame=frame,t.init()}init(){let t=this;t.width=t.scene.textures.getFrame(t.texture,t.frame).width,t.height=t.scene.textures.getFrame(t.texture,t.frame).height,null==t.y&&t.setYbottom(),t.blitter=t.scene.add.blitter(0,t.y,t.texture,t.frame),t.img1=t.blitter.create(0,0),t.img2=t.blitter.create(t.width-t.overlap,0)}getDistance(speed,deltaTime){return deltaTime*speed/1e3}setYbottom(){this.y=this.scene.game.config.height-this.height}update(delta){let t=this;t.img1.x+=t.getDistance(t.speed,delta),t.img2.x+=t.getDistance(t.speed,delta),t.img1.x<-t.width&&(t.img1.x=t.width+t.img2.x-t.overlap),t.img2.x<-t.width&&(t.img2.x=t.width+t.img1.x-t.overlap)}}