class ScrollingLayer{constructor(scene,y,speedX,overlapX,texture,frame){let t=this;t.scene=scene,t.y=y,t.speed=speedX,t.overlap=overlapX,t.texture=texture,t.frame=frame,t.init()}init(){let t=this;t.width=t.scene.textures.getFrame(t.texture,t.frame).width,t.blitter=t.scene.add.blitter(0,t.y,t.texture,t.frame),t.x=0,t.img1=t.blitter.create(t.x,0),t.img2=t.blitter.create(t.width-t.overlap,0)}getDistance(speed,deltaTime){return deltaTime*speed/1e3}update(delta){let t=this;t.img1.x+=t.getDistance(t.speed,delta),t.img2.x+=t.getDistance(t.speed,delta),t.img1.x<-t.width&&(t.img1.x=t.width+t.img2.x-t.overlap),t.img2.x<-t.width&&(t.img2.x=t.width+t.img1.x-t.overlap)}}